var videoContainers=document.querySelectorAll(".info-item"),wrapper=document.querySelector(".wrapper"),backLayer=document.querySelector(".back-layer");function transformation(e){var t=document.documentElement,a=t.clientWidth,r=t.clientHeight,s=a/2,n=r/2,o=e.clientWidth,i=e.clientHeight;return{translate:{x:s-(o/2+e.offsetLeft),y:n-(i/2+e.offsetTop-wrapper.scrollTop)},scale:Math.min(a/o,r/i)}}function zoomVideo(e){var t=videoContainers[parseInt(e,10)-1];if(wrapper.classList.contains("fullscreen"))t.style.transform="translate(0px, 0px) scale(1)",backLayer.classList.remove("active"),setTimeout(function(){t.classList.remove("active"),wrapper.classList.remove("fullscreen")},300);else{var a=transformation(t),r=a.translate,s=a.scale;t.style.transform="translate("+r.x+"px, "+r.y+"px) scale("+s+")",t.classList.add("active"),wrapper.classList.add("fullscreen"),backLayer.classList.add("active")}}videos.forEach(function(e){e.addEventListener("mousedown",function(){var e=this.dataset.videoNum;requestAnimationFrame(function(){zoomVideo(e)})})});